// Database.js 4
import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPen, faTrashAlt } from "@fortawesome/free-solid-svg-icons";

class TeamMember extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isEditing: false,
      updatedData: { ...props }, // Copy the props to initialize the updatedData
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState((prevState) => ({
      updatedData: {
        ...prevState.updatedData,
        [name]: value,
      },
    }));
  };

  handleEdit = () => {
    this.setState({ isEditing: true });
  };

  handleSave = () => {
    // Send a PUT request to update the team member in the database
    fetch(`http://localhost:3001/users/${this.props._id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state.updatedData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Team member updated:", data);
        this.setState({ isEditing: false });
      })
      .catch((error) => console.error("Error updating team member:", error));
  };

  handleDelete = () => {
    const { _id } = this.props;

    // Send a DELETE request to remove the team member from the database
    fetch(`http://localhost:3001/users/${_id}`, {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Team member deleted:", data);

        // Fetch updated data after successful deletion
        this.props.fetchUpdatedData();
      })
      .catch((error) => console.error("Error deleting team member:", error));
  };

  handleImageUpload = (event) => {
    const file = event.target.files[0];

    // Validate the file type
    if (file && (file.type === "image/jpeg" || file.type === "image/png")) {
      // Read the file as a data URL and set the img state with the data URL
      const reader = new FileReader();
      reader.onloadend = () => {
        this.setState((prevState) => ({
          updatedData: {
            ...prevState.updatedData,
            img: reader.result,
          },
        }));
      };
      reader.readAsDataURL(file);
    } else {
      // Invalid file type, reset the image state
      this.setState((prevState) => ({
        updatedData: {
          ...prevState.updatedData,
          img: "",
        },
      }));
    }
  };

  render() {
    const { members, searchQuery, sortOption } = this.state;
  
    // Filter and sort the members based on searchQuery and sortOption
    const filteredMembers = members.filter((member) =>
      member.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  
    // Sort the filteredMembers based on sortOption
    filteredMembers.sort((a, b) => {
      const aValue = a[sortOption].toLowerCase();
      const bValue = b[sortOption].toLowerCase();
      return aValue.localeCompare(bValue, undefined, { sensitivity: 'base' });
    });
  
    return (
      <div>
      <div className="navbar bg-dark rounded text-white d-flex">
        <div className="container-fluid d-flex justify-content-between ">
          <h1 style={{ marginLeft: "40%" }}>List of Stagiaires</h1>
          {/* Search Bar */}
          <div className="input-group ml-auto">
            <input
              type="text"
              className="form-control"
              placeholder="Search by Name"
              value={searchQuery}
              onChange={this.handleSearchChange}
            />
          </div>
        </div>
      </div>
  
        <div className="row">
          <div className="col-md-12">
            {/* Search Bar */}
  
            {/* Sort Options */}
            <div className="form-group">
              <label>Sort By:</label>
              <select
                className="form-control"
                value={sortOption}
                onChange={this.handleSortChange}
              >
                <option value="name">Name</option>
                <option value="position">Position</option>
                <option value="website">Website</option>
              </select>
            </div>
          </div>
        </div>
  
        <div className="row">
          {filteredMembers.map((member) => (
            <TeamMember
              key={member._id}
              _id={member._id}
              img={member.img}
              name={member.name}
              position={member.position}
              phone={member.phone}
              email={member.email}
              website={member.website}
              fetchUpdatedData={this.fetchUpdatedData}
            />
          ))}
        </div>
      </div>
    );
  }
  
}

export default TeamMember;
