// Database.js 4
import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPen, faTrashAlt } from "@fortawesome/free-solid-svg-icons";

class TeamMember extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isEditing: false,
      updatedData: { ...props }, // Copy the props to initialize the updatedData
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState((prevState) => ({
      updatedData: {
        ...prevState.updatedData,
        [name]: value,
      },
    }));
  };

  handleEdit = () => {
    this.setState({ isEditing: true });
  };

  handleSave = () => {
    // Send a PUT request to update the team member in the database
    fetch(`http://localhost:3001/users/${this.props._id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state.updatedData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Team member updated:", data);
        this.setState({ isEditing: false });
      })
      .catch((error) => console.error("Error updating team member:", error));
  };

  handleDelete = () => {
    const { _id } = this.props;

    // Send a DELETE request to remove the team member from the database
    fetch(`http://localhost:3001/users/${_id}`, {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Team member deleted:", data);

        // Fetch updated data after successful deletion
        this.props.fetchUpdatedData();
      })
      .catch((error) => console.error("Error deleting team member:", error));
  };

  handleImageUpload = (event) => {
    const file = event.target.files[0];

    // Validate the file type
    if (file && (file.type === "image/jpeg" || file.type === "image/png")) {
      // Read the file as a data URL and set the img state with the data URL
      const reader = new FileReader();
      reader.onloadend = () => {
        this.setState((prevState) => ({
          updatedData: {
            ...prevState.updatedData,
            img: reader.result,
          },
        }));
      };
      reader.readAsDataURL(file);
    } else {
      // Invalid file type, reset the image state
      this.setState((prevState) => ({
        updatedData: {
          ...prevState.updatedData,
          img: "",
        },
      }));
    }
  };


  render() {
    const { isEditing, updatedData } = this.state;
    const { img, name, position, phone, email, website } = updatedData;

    let borderStyle = "none";
    let borderColor = "";

    if (website === "Accepted") {
      borderStyle = "solid";
      borderColor = "#085a00"; // Green
    } else if (website === "Refused") {
      borderStyle = "dashed";
      borderColor = "#860000"; // Red
    }

    return (
      <div className="col-md-3 col-sm-4">
        <div
          className="card"
          style={{
            borderStyle: borderStyle,
            borderColor: borderColor,
            borderWidth: "3px",
          }}
        >
          <div className="card-header">
            {/* ... (rest of the code) */}
          </div>

          <div className="card-body">
            {/* ... (rest of the code) */}
          </div>
          <div className="card-footer">
            {/* ... (rest of the code) */}
          </div>
        </div>
      </div>
    );
  }
}

export default TeamMember;
