//Stagiere_list.js 1
import React, { Component } from "react";
import TeamMember from "./Database";

class Team extends Component {
  constructor() {
    super();
    this.state = {
      members: [],
    };
  }
  fetchUpdatedData = () => {
    fetch("http://localhost:3001/users")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ members: data });
      })
      .catch((error) =>
        console.error("Error fetching member information:", error)
      );
  };
  componentDidMount() {
    fetch("http://localhost:3001/users")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ members: data });
      })
      .catch((error) =>
        console.error("Error fetching member information:", error)
      );
  }

  render() {
    const { members } = this.state;
    return (
      <div>
        <div className="navbar bg-dark rounded text-white d-flex justify-content-center align-items-center">
          <h2>List of Stagiaires</h2>
        </div>
        <div className="row">
          {members.map((member) => (
            <TeamMember
              key={member._id}
              _id={member._id}
              img={member.img}
              name={member.name}
              position={member.position}
              phone={member.phone}
              email={member.email}
              website={member.website}
              fetchUpdatedData={this.fetchUpdatedData} 
            />
          ))}
        </div>
      </div>
    );
  }
}

export default Team;
